using APIBomberos.Clases.Conexión;using APIBomberos.Respuestas;using System.Data.SqlClient;using System.Data;namespace APIBomberos.Clases{    public class ClsExamen    {        public ClsExamen() { }        public string MostrarExamen(string usuario, string idtema)        {            string Result = "";            using (SqlConnection conn = new SqlConnection(ClsConexion.Conexion))            {                conn.Open();                SqlDataAdapter da = new SqlDataAdapter();                DataTable dt = new DataTable();                da.SelectCommand = new SqlCommand("[USP_GET_Examen]", conn);                da.SelectCommand.CommandTimeout = 0;                da.SelectCommand.Parameters.AddWithValue("@TipoOperacion", "S");                da.SelectCommand.Parameters.AddWithValue("@idusario", usuario);
                da.SelectCommand.Parameters.AddWithValue("@idtema ", idtema);                da.SelectCommand.CommandType = CommandType.StoredProcedure;                da.Fill(dt);                Result = Clsjsontablcs.toJson(dt);                conn.Dispose();                conn.Close();            }            return Result;        }    }}