using System.Data.SqlClient;using System.Data;using System.Text;using APIBomberos.Clases.Conexión;using APIBomberos.Respuestas;namespace APIBomberos.Clases{    public class Clsgetusu    {        public Clsgetusu() { }        public string listausarios()        {            string Result = "";            using (SqlConnection conn = new SqlConnection(ClsConexion.Conexion))            {                conn.Open();                SqlDataAdapter da = new SqlDataAdapter();                DataTable dt = new DataTable();                da.SelectCommand = new SqlCommand("[USP_GET_USUARIO]", conn);                da.SelectCommand.CommandTimeout = 0;                da.SelectCommand.Parameters.AddWithValue("@TipoOperacion", "U");                da.SelectCommand.CommandType = CommandType.StoredProcedure;                da.Fill(dt);                Result = Clsjsontablcs.toJson(dt);                conn.Dispose();                conn.Close();            }            return Result;        }        public string listausarioscurso()        {            string Result = "";            using (SqlConnection conn = new SqlConnection(ClsConexion.Conexion))            {                conn.Open();                SqlDataAdapter da = new SqlDataAdapter();                DataTable dt = new DataTable();                da.SelectCommand = new SqlCommand("[USP_GET_USUARIO]", conn);                da.SelectCommand.CommandTimeout = 0;                da.SelectCommand.Parameters.AddWithValue("@TipoOperacion", "A");                da.SelectCommand.CommandType = CommandType.StoredProcedure;                da.Fill(dt);                Result = Clsjsontablcs.toJson(dt);                conn.Dispose();                conn.Close();            }            return Result;        }    }}